import React, { Component } from 'react';
import './App.css';
import QuizQuestion from './QuizQuestion'; // Import the QuizQuestion component
import QuizEnd from './QuizEnd'; // Import the QuizEnd component

// Import your quiz data here
import quizData from './quiz_data.json';

class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quiz_position: 1
    };
    // Your class implementation goes here
  }

  render() {
    // Access the current quiz question based on quiz_position
    const currentQuestion = quizData.quiz_questions[this.state.quiz_position - 1];

    // Check if it's the end of the quiz
    const isQuizEnd = this.state.quiz_position > quizData.quiz_questions.length;

    return (
      <div>
        {isQuizEnd && <QuizEnd />} {/* Render QuizEnd component if it's the end of the quiz */}
        {isQuizEnd || <QuizQuestion quiz_question={currentQuestion} />} {/* Render QuizQuestion unless it's the end of the quiz */}
      </div>
    );
  }
}

export default Quiz;
