import React, { Component } from 'react';
import QuizQuestionButton from './QuizQuestionButton'; // Import the QuizQuestionButton component

class QuizQuestion extends Component {
 render() {
    const firstAnswer = this.props.quiz_question.answer_options[0];

    return (
      <main>
        <section>
          <p>{this.props.quiz_question.instruction_text}</p>
        </section>
        <section className="buttons">
          <ul>
            <QuizQuestionButton button_text={firstAnswer} /> {/* Pass the button_text prop */}
            {/* Add other answer options as needed */}
          </ul>
        </section>
      </main>
    );
  }
}

export default QuizQuestion;
