import React, { Component } from 'react';
import './App.css';
import QuizQuestion from './QuizQuestion'; // Import the QuizQuestion component

// Import your quiz data here
import quizData from './quiz_data.json';

class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quiz_position: 1
    };
    // Your class implementation goes here
  }

  render() {
    // Access the instruction text from quizData (you need to specify the question index)
    const instructionText = quizData.quiz_questions[this.state.quiz_position - 1].instruction_text;

    return (
      <div>
        <div className="QuizQuestion">
          {instructionText}
        </div>
      </div>
    );
  }
}

export default Quiz;
