import React, { Component } from 'react';
import './App.css';
import QuizQuestion from './QuizQuestion'; // Import the QuizQuestion component

// Import your quiz data here
import quizData from './quiz_data.json';

class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quiz_position: 1
    };
    // Your class implementation goes here
  }

  render() {
    // Access the current quiz question based on quiz_position
    const currentQuestion = quizData.quiz_questions[this.state.quiz_position - 1];

    return (
      <QuizQuestion quiz_question={currentQuestion} /> 
    );
  }
}

export default Quiz;
